package main

import (
	"encoding/hex"
	"fmt"
	"github.com/google/gopacket"
	"github.com/google/gopacket/layers"
	"github.com/google/gopacket/pcapgo"
	"log"
	"os"
	"time"
)

func main() {
	str1 := ``
	escapedStr1, err := hex.DecodeString(str1)
	if err != nil {
		fmt.Println("Ошибка:", err)
		return
	}
	// Извлечение типа канала из заголовка

	// Создание нового pcap файла
	outputFile, err := os.Create("output.pcap")
	if err != nil {
		log.Fatal(err)
	}
	defer outputFile.Close()
	writer := pcapgo.NewWriter(outputFile)
	writer.WriteFileHeader(65535, layers.LinkTypeLinuxSLL)

	// Запись данных дампа в новый pcap файл
	err = writer.WritePacket(gopacket.CaptureInfo{
		Timestamp:     time.Now(),
		CaptureLength: len(escapedStr1),
		Length:        len(escapedStr1),
	}, escapedStr1)
	if err != nil {
		log.Fatal(err)
	}
	str2 := ``
	escapedStr2, err := hex.DecodeString(str2)
	if err != nil {
		fmt.Println("Ошибка:", err)
		return
	}
	err = writer.WritePacket(gopacket.CaptureInfo{
		Timestamp:     time.Now(),
		CaptureLength: len(escapedStr2),
		Length:        len(escapedStr2),
	}, escapedStr2)
	if err != nil {
		log.Fatal(err)
	}

	fmt.Println("Дамп успешно записан в output.pcap")
}
